mapper.select:
SELECT *
FROM member
WHERE id = #{id} AND pw = #{pw}

mapper.insert:
INSERT INTO member (
    id, name, nickname, pw, birth_date, email_id, email_domain,
    social_platform, social_id, phone, gender, join_date,
    state, email_ad, email_verified
) VALUES (
    #{id}, #{name}, #{nickname}, #{pw}, #{birthDate}, #{emailId}, #{emailDomain},
    #{socialPlatform}, #{socialId}, #{phone}, #{gender}, SYSDATE,
    #{state}, #{emailAd}, #{emailVerified}
)
