mapper.select:
SELECT
CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM WISHLIST
WHERE G_NUM
= #{gNum}
AND ID = #{id}

SELECT * FROM (
SELECT g.*, ROWNUM rn FROM (
SELECT g.*
FROM wishlist w
left JOIN game_info g ON w.g_num = g.g_num
WHERE w.id = #{id}
ORDER BY

	 g.gi_title ASC 
	 g.gi_fprice ASC 
	 g.gi_date DESC 

) g
WHERE ROWNUM <= #{offset} + #{limit}
)
WHERE rn > #{offset}

SELECT * FROM (
SELECT g.*, ROWNUM rn FROM (
SELECT g.*
FROM wishlist w
LEFT OUTER JOIN game_info g ON w.g_num = g.g_num
WHERE w.id = #{id}
AND (LOWER(g.gi_title) LIKE LOWER(#{keyword})
OR LOWER(g.gi_platform) LIKE LOWER(#{keyword}))
ORDER BY

	 g.gi_title ASC 
	 g.gi_fprice ASC 

) g
WHERE ROWNUM <= #{offset} + #{limit}
)
WHERE rn > #{offset}

SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM wishlist
WHERE g_num = #{gNum} AND id = #{id}
